mapper.insert:

  SELECT LAST_INSERT_ID()

insert into tuser (id, loginname, password
  )
values (#{id,jdbcType=INTEGER}, #{loginname,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}
  )

mapper.update:
